require(["gitbook", "jQuery"], function(gitbook, $) {

    gitbook.events.bind("start", function(e, config) {
      console.log('start top nav');
    });

    gitbook.events.bind("page.change", function(e) {
      function createHeader() {
        return '<div class="top-nav-header w-nav" data-animation="default" data-collapse="medium" data-doc-height="1" data-duration="200" data-no-scroll="1"> ' +
                 '<div class="w-container"> ' + 
                    '<a class="brand w-nav-brand w--current" href="https://clife.cn/#/" target="_blank"> <img class="top-nav-logo" src="/images/logo54x54.png"> <span class="logo-title">C-Life 开放平台</span> </a> ' +
                    '<nav class="navmenu w-nav-menu" role="navigation"> ' + 
                      '<a class="top-nav-navlink w-nav-link" href="/source/zh-cn/overview/index.html">平台概述</a>'+
                      '<a class="top-nav-navlink w-nav-link" href="/source/zh-cn/product/index.html">产品开发</a>'+
                      '<a class="top-nav-navlink w-nav-link" href="/source/zh-cn/app/index.html">APP开发</a>'+
                      '<a class="top-nav-navlink w-nav-link" href="/source/zh-cn/device/index.html">硬件开发</a>'+
                      '<a class="top-nav-navlink w-nav-link" href="/source/zh-cn/cloudAPI/cloudAPI.html">云API</a>'+
                      '<a class="top-nav-navlink w-nav-link" href="/source/zh-cn/dataservice/index.md">数据服务</a>'+
                      '<a class="top-nav-navlink w-nav-link" href="/source/zh-cn/datapush/index.html">推送服务</a>'+
                      '</nav>'+
                  '</div>'+
                '</div>';
    }
      // Get configuration.
      var headerTitle = 'NEW HEADER';

      $('.book-body').css('top', '70px');
      $('.book-summary').css('top', '70px');
      $('.book-header').css({'top': '70px', 'border-top': '1px solid #ededed'});

      // Add customize header html.
      var $header = $('<div class="custom-header"></div>');
      var $headerWrapper = $('<div class="header-element-wrapper"></div>');
      var $link = $(createHeader());
      $headerWrapper.append($link);
      //var $headerTitle = $('<div class="header-text">' + headerTitle + '</div>');

      //$headerWrapper.append($headerTitle);
      $header.append($headerWrapper);
      $('.book-summary').before($header);
    });
});